import React, { useState } from "react";
import { useNavigate } from "react-router";
import './App.css';
 
export default function Create() {
 const [form, setForm] = useState({
   firstName: "",
   lastName: "",
   relationship: "",
 });
 const navigate = useNavigate();
 
 // These methods will update the state properties.
 function updateForm(value) {
   return setForm((prev) => {
     return { ...prev, ...value };
   });
 }
 
 // This function will handle the submission.
 async function onSubmit(e) {
   e.preventDefault();
 
   // When a post request is sent to the create url, we'll add a new record to the database.
   const newPerson = { ...form };
 
   await fetch("http://localhost:5000/record/add", {
     method: "POST",
     headers: {
       "Content-Type": "application/json",
     },
     body: JSON.stringify(newPerson),
   })
   .catch(error => {
     window.alert(error);
     return;
   });
 
   setForm({ firstName: "", lastName: "", contribution: "", relationship: ""});
   navigate("/");
 }
 
 // This following section will display the form that takes the input from the user.
 return (
   <div>
     <h3 className='center-title'>Create New Record</h3>
     <div className="form">
     <form className="cap-form-formatting" onSubmit={onSubmit}>
      
       <div className="cap-options-spacing form-group">
         <label htmlFor="name">First Name</label>
         <input
           type="text"
           className="form-control"
           id="firstName"
           value={form.firstName}
           onChange={(e) => updateForm({ firstName: e.target.value })}
         />
       </div>
       <div className="cap-options-spacing form-group">
         <label htmlFor="position">Last Name</label>
         <input
           type="text"
           className="form-control"
           id="lastName"
           value={form.lastName}
           onChange={(e) => updateForm({ position: e.target.value })}
         />
       </div>
       <div className="cap-options-spacing form-group">
         <div className="form-check form-check-inline">
           <input
             className="form-check-input"
             type="radio"
             name="relationshipOptions"
             id="relationshipFriend"
             value="Friend"
             checked={form.level === "Friend"}
             onChange={(e) => updateForm({ level: e.target.value })}
           />
           <label htmlFor="relationshipFriend" className="form-check-label">Friend</label>
         </div>
         <div className="form-check form-check-inline">
           <input
             className="form-check-input"
             type="radio"
             name="relationshipOptions"
             id="relationshipFamily"
             value="Family"
             checked={form.level === "Family"}
             onChange={(e) => updateForm({ level: e.target.value })}
           />
           <label htmlFor="RelationshipFamily" className="form-check-label">Family</label>
         </div>
         <div className="form-check form-check-inline">
           <input
             className="form-check-input"
             type="radio"
             name="relationshipOptions"
             id="relationshipFamily"
             value="Family"
             checked={form.level === "Family"}
             onChange={(e) => updateForm({ level: e.target.value })}
           />
           <label htmlFor="relationshipWorkRelated" className="form-check-label">Work Related</label>
         </div>
       </div>
       <div className="form-group">
         <input
           type="submit"
           value="Create person"
           className="btn btn-primary"
         />
       </div>
       
     </form>
     </div>
   </div>
 );
}
