import React from "react";
 
// We use Route in order to define the different routes of our application
import { Route, Routes } from "react-router-dom";
 
// We import all the components we need in our app
import Navbar from "./components/navbar";
import RecordList from "./components/records-panel";
import Edit from "./components/edit-profile";
import Create from "./components/create-a-profile";
 
const App = () => {
 return (
   <div>
     <Navbar />
     <Routes>
       <Route exact path="/" element={<RecordList />} />
       <Route path="/edit-profile/:id" element={<Edit />} />
       <Route path="/create-a-profile" element={<Create />} />
     </Routes>
   </div>
 );
};
 
export default App;
